import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./TicTacToe.css";

const TicTacToe = () => {
  const [board, setBoard] = useState(Array(9).fill(null));

  const checkWinner = (board) => {
    const winningCombos = [
      [0, 1, 2], [3, 4, 5], [6, 7, 8],
      [0, 3, 6], [1, 4, 7], [2, 5, 8],
      [0, 4, 8], [2, 4, 6]
    ];
    
    for (let combo of winningCombos) {
      const [a, b, c] = combo;
      if (board[a] && board[a] === board[b] && board[a] === board[c]) {
        return board[a];
      }
    }
    return null;
  };

  const handleClick = (index) => {
    if (board[index] || checkWinner(board)) return;
    const newBoard = [...board];
    newBoard[index] = "X";
    setBoard(newBoard);
    setTimeout(() => botMove(newBoard), 500);
  };

  const botMove = (currentBoard) => {
    const emptyCells = currentBoard.map((cell, index) => cell === null ? index : null).filter(index => index !== null);
    
    // 1. Попытка победить
    const winningMove = findBestMove(currentBoard, "O");
    if (winningMove !== -1) {
      currentBoard[winningMove] = "O";
      setBoard([...currentBoard]);
      return;
    }
    
    // 2. Блокировка победы игрока
    const blockingMove = findBestMove(currentBoard, "X");
    if (blockingMove !== -1) {
      currentBoard[blockingMove] = "O";
      setBoard([...currentBoard]);
      return;
    }
  
    // 3. Случайный ход
    const randomIndex = emptyCells[Math.floor(Math.random() * emptyCells.length)];
    currentBoard[randomIndex] = "O";
    setBoard([...currentBoard]);
  };
  
  // Функция для поиска лучшего хода (победа или блокировка)
  const findBestMove = (board, player) => {
    const winningCombos = [
      [0, 1, 2], [3, 4, 5], [6, 7, 8],
      [0, 3, 6], [1, 4, 7], [2, 5, 8],
      [0, 4, 8], [2, 4, 6]
    ];
  
    for (let combo of winningCombos) {
      const [a, b, c] = combo;
      const cells = [board[a], board[b], board[c]];
      const emptyCells = combo.filter(index => board[index] === null);
  
      // Если два игрока заняли два поля, а одно пустое, это потенциальный выигрыш или блокировка
      if (cells.filter(cell => cell === player).length === 2 && emptyCells.length === 1) {
        return emptyCells[0];  // Возвращаем индекс пустой клетки
      }
    }
    
    return -1;  // Нет хорошего хода
  };
  

  const winner = checkWinner(board);
  return (
    <div className="game-container">
      <Link to="/" className="back-button">⬅ Назад</Link>
      <h2>Крестики-нолики</h2>
      <div className="board">
        {board.map((cell, index) => (
          <div key={index} className="cell" onClick={() => handleClick(index)}>
            {cell}
          </div>
        ))}
      </div>
      {winner && <h3>Победитель: {winner}</h3>}
    </div>
  );
};

export default TicTacToe;
