import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./TicTacToe.css";

// Уровни сложности
const LEVELS = {
  EASY: 'easy',
  MEDIUM: 'medium',
  HARD: 'hard',
};

const TicTacToe = () => {
  const [board, setBoard] = useState(Array(9).fill(null));
  const [winner, setWinner] = useState(null);
  const [difficulty, setDifficulty] = useState(LEVELS.MEDIUM);
  const [playerScore, setPlayerScore] = useState(0);
  const [botScore, setBotScore] = useState(0);

  const checkWinner = (board) => {
    const winningCombos = [
      [0, 1, 2], [3, 4, 5], [6, 7, 8],
      [0, 3, 6], [1, 4, 7], [2, 5, 8],
      [0, 4, 8], [2, 4, 6]
    ];

    for (let combo of winningCombos) {
      const [a, b, c] = combo;
      if (board[a] && board[a] === board[b] && board[a] === board[c]) {
        return board[a];
      }
    }
    return null;
  };

  const minimax = (board, depth, isMaximizing) => {
    const winner = checkWinner(board);
    if (winner === 'X') return -10 + depth;
    if (winner === 'O') return 10 - depth;
    if (board.every(cell => cell !== null)) return 0; // Ничья

    const emptyCells = board.map((cell, index) => cell === null ? index : null).filter(index => index !== null);

    if (isMaximizing) {
      let best = -Infinity;
      for (let index of emptyCells) {
        board[index] = 'O';
        const score = minimax(board, depth + 1, false);
        board[index] = null;
        best = Math.max(score, best);
      }
      return best;
    } else {
      let best = Infinity;
      for (let index of emptyCells) {
        board[index] = 'X';
        const score = minimax(board, depth + 1, true);
        board[index] = null;
        best = Math.min(score, best);
      }
      return best;
    }
  };

  const findBestMove = (board) => {
    let bestMove = -1;
    let bestValue = -Infinity;

    for (let i = 0; i < 9; i++) {
      if (board[i] === null) {
        board[i] = 'O';
        const moveValue = minimax(board, 0, false);
        board[i] = null;
        if (moveValue > bestValue) {
          bestValue = moveValue;
          bestMove = i;
        }
      }
    }

    return bestMove;
  };

  const botMove = (currentBoard) => {
    const emptyCells = currentBoard.map((cell, index) => cell === null ? index : null).filter(index => index !== null);

    if (difficulty === LEVELS.HARD) {
      const bestMove = findBestMove(currentBoard);
      if (bestMove !== -1) {
        currentBoard[bestMove] = "O";
        setBoard([...currentBoard]);
        return;
      }
    }

    if (difficulty === LEVELS.MEDIUM) {
      const blockingMove = findBestMove(currentBoard, "X");
      if (blockingMove !== -1) {
        currentBoard[blockingMove] = "O";
        setBoard([...currentBoard]);
        return;
      }
    }

    const randomIndex = emptyCells[Math.floor(Math.random() * emptyCells.length)];
    currentBoard[randomIndex] = "O";
    setBoard([...currentBoard]);
  };

  const handleClick = (index) => {
    if (board[index] || winner) return;
    const newBoard = [...board];
    newBoard[index] = "X";
    setBoard(newBoard);

    const gameWinner = checkWinner(newBoard);
    if (gameWinner) {
      setWinner(gameWinner);
      if (gameWinner === "X") setPlayerScore(playerScore + 1);
      else setBotScore(botScore + 1);
    } else {
      setTimeout(() => botMove(newBoard), 500);
    }
  };

  const restartGame = () => {
    setBoard(Array(9).fill(null));
    setWinner(null);
  };

  return (
    <div className="game-container">
      <Link to="/" className="back-button">⬅ Назад</Link>
      <h2>Крестики-нолики</h2>

      <div className="difficulty">
        <label>Сложность:</label>
        <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
          <option value={LEVELS.EASY}>Легкий</option>
          <option value={LEVELS.MEDIUM}>Средний</option>
          <option value={LEVELS.HARD}>Сложный</option>
        </select>
      </div>

      <div className="board">
        {board.map((cell, index) => (
          <div
            key={index}
            className={`cell ${winner && board[index] ? 'winner' : ''}`}
            onClick={() => handleClick(index)}
          >
            {cell}
          </div>
        ))}
      </div>

      {winner && <h3>{winner === "X" ? "Победитель: Игрок" : "Победитель: Бот"}</h3>}
      <div className="stats">
        <p>Игрок: {playerScore} | Бот: {botScore}</p>
      </div>

      <button className="restart-button" onClick={restartGame}>Перезапустить игру</button>
    </div>
  );
};

export default TicTacToe;
