import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import "./styles.css";

// –ó–∞–≥–ª—É—à–∫–∏ –¥–ª—è –∏–≥—Ä
const TicTacToe = () => <h2 className="text-xl font-semibold">–ö—Ä–µ—Å—Ç–∏–∫–∏-–Ω–æ–ª–∏–∫–∏ (–≤ —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–µ)</h2>;
const RockPaperScissors = () => <h2 className="text-xl font-semibold">–ö–∞–º–µ–Ω—å, –Ω–æ–∂–Ω–∏—Ü—ã, –±—É–º–∞–≥–∞ (–≤ —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–µ)</h2>;
const MemoryGame = () => <h2 className="text-xl font-semibold">–ò–≥—Ä–∞ –Ω–∞ –ø–∞–º—è—Ç—å (–≤ —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–µ)</h2>;
const ReactionGame = () => <h2 className="text-xl font-semibold">–ò–≥—Ä–∞ –Ω–∞ —Ä–µ–∞–∫—Ü–∏—é (–≤ —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–µ)</h2>;

const Home = () => {
  const [isFirstVisit, setIsFirstVisit] = useState(false);

  useEffect(() => {
    const visited = localStorage.getItem("visited");
    if (!visited) {
      setIsFirstVisit(true);
      localStorage.setItem("visited", "true");
    }
  }, []);

  return (
    <div className="container">
      {isFirstVisit && (
        <div className="welcome-message">
          <h2>–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ –ú–∏–Ω–∏-–∏–≥—Ä–æ–≤–æ–π —Ö–∞–±! üéÆ</h2>
          <p>–í—ã–±–∏—Ä–∞–π—Ç–µ –∏–≥—Ä—É –∏ –Ω–∞—Å–ª–∞–∂–¥–∞–π—Ç–µ—Å—å!</p>
        </div>
      )}
      <h1>üéÆ –ú–∏–Ω–∏-–∏–≥—Ä–æ–≤–æ–π —Ö–∞–±</h1>
      <nav className="nav-links">
        <Link to="/tic-tac-toe">–ö—Ä–µ—Å—Ç–∏–∫–∏-–Ω–æ–ª–∏–∫–∏</Link>
        <Link to="/rock-paper-scissors">–ö–∞–º–µ–Ω—å, –Ω–æ–∂–Ω–∏—Ü—ã, –±—É–º–∞–≥–∞</Link>
        <Link to="/memory-game">–ò–≥—Ä–∞ –Ω–∞ –ø–∞–º—è—Ç—å</Link>
        <Link to="/reaction-game">–ò–≥—Ä–∞ –Ω–∞ —Ä–µ–∞–∫—Ü–∏—é</Link>
      </nav>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/tic-tac-toe" element={<TicTacToe />} />
        <Route path="/rock-paper-scissors" element={<RockPaperScissors />} />
        <Route path="/memory-game" element={<MemoryGame />} />
        <Route path="/reaction-game" element={<ReactionGame />} />
      </Routes>
    </Router>
  );
};

export default App;
